sqlMap.select:
SELECT A.UNITID, A.DISPLAYTYPE, B.ADMIN FROM TNE_ITEM_DETAIL A, (
SELECT E.ITEMID, E.ITEMPID, E.METHODID, REPLACE(wm_concat(F.NAME), ',', ', ') AS ADMIN FROM TNE_ITEM_ADMIN E, TCT_ADMIN F WHERE E.ADMINID = F.ADMINID (+) GROUP BY E.ITEMID, E.METHODID, E.ITEMPID
) B WHERE A.ITEMID = #itemID# AND A.METHODID = #methodID# AND A.ITEMPID = #itemPID# AND A.ITEMID = B.ITEMID AND A.ITEMPID = B.ITEMPID AND A.METHODID = B.METHODID
